import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

const LoginTest = () => {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  // Simulate the login response you provided
  const simulateLogin = async () => {
    setLoading(true);

    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Simulate the login response format
    const mockResponse = {
      accessToken: "eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJ0ZXN0dXNlciIsImlhdCI6MTc1ODk2MzU4OSwiZXhwIjoxNzU5MDQ5OTg5fQ.YJitT1OVeBIVqpdFL8eItyO0hkV0TLeMOix31TAfcltdnRENIHIbZv0QqEYkJCyllO9Ppl53D_JQdDXKIkM6gw",
      type: "Bearer",
      id: 1,
      username: "testuser",
      email: "testuser@example.com",
      firstName: "Test",
      lastName: "User",
      phone: "+1234567890",
      address: "123 Test Street, Test City",
      role: "ADMIN",
      roles: ["ADMIN", "USER"]
    };

    // Manually set the user data and token
    const { accessToken, type, id, username, email, firstName, lastName, phone, address, role, roles } = mockResponse;

    const userData = {
      id,
      username,
      email,
      firstName,
      lastName,
      phone,
      address,
      role,
      roles
    };

    // Store in localStorage
    localStorage.setItem('authToken', accessToken);
    localStorage.setItem('user', JSON.stringify(userData));

    // Trigger a page reload to test the authentication flow
    window.location.reload();

    setLoading(false);
  };

  return (
    <div style={{ 
      position: 'fixed', 
      top: '20px', 
      right: '20px', 
      zIndex: 1000,
      background: 'white',
      padding: '20px',
      borderRadius: '8px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
      border: '1px solid #e9ecef'
    }}>
      <h3 style={{ margin: '0 0 15px 0', color: '#2c3e50' }}>Login Test</h3>
      <p style={{ margin: '0 0 15px 0', color: '#6c757d', fontSize: '14px' }}>
        Click to simulate login with your provided response data
      </p>
      <button
        onClick={simulateLogin}
        disabled={loading}
        style={{
          background: loading ? '#bdc3c7' : '#3498db',
          color: 'white',
          border: 'none',
          padding: '10px 20px',
          borderRadius: '6px',
          cursor: loading ? 'not-allowed' : 'pointer',
          fontWeight: '600',
          fontSize: '14px'
        }}
      >
        {loading ? 'Simulating...' : 'Simulate Login'}
      </button>
    </div>
  );
};

export default LoginTest;
