import React from 'react';
import { 
  Home, 
  Users, 
  User, 
  Grid3X3, 
  Building, 
  Grid, 
  FileText, 
  Mail, 
  List, 
  Wrench, 
  UserCheck, 
  BarChart3, 
  Settings,
  ChevronLeft,
  ChevronDown
} from 'lucide-react';

const Sidebar = ({ collapsed, onToggle }) => {
  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: Home, active: true },
    { id: 'user', label: 'User', icon: Users },
    { id: 'owner', label: 'Owner', icon: User },
    { 
      id: 'tenant', 
      label: 'Tenant', 
      icon: Grid3X3, 
      hasDropdown: true,
      children: [
        { id: 'tenant-list', label: 'Tenant List' },
        { id: 'tenant-add', label: 'Add Tenant' }
      ]
    },
    { 
      id: 'apartment', 
      label: 'Apartment', 
      icon: Building, 
      hasDropdown: true,
      children: [
        { id: 'apartment-list', label: 'Apartment List' },
        { id: 'apartment-add', label: 'Add Apartment' }
      ]
    },
    { 
      id: 'amenities', 
      label: 'Amenities', 
      icon: Grid, 
      hasDropdown: true,
      children: [
        { id: 'amenities-list', label: 'Amenities List' },
        { id: 'amenities-booking', label: 'Bookings' }
      ]
    },
    { 
      id: 'bills', 
      label: 'Bills', 
      icon: FileText, 
      hasDropdown: true,
      children: [
        { id: 'bills-list', label: 'Bills List' },
        { id: 'bills-create', label: 'Create Bill' }
      ]
    },
    { id: 'tickets', label: 'Tickets', icon: Mail },
    { id: 'notice', label: 'Notice Board', icon: List },
    { 
      id: 'services', 
      label: 'Services', 
      icon: Wrench, 
      hasDropdown: true,
      children: [
        { id: 'services-list', label: 'Services List' },
        { id: 'services-request', label: 'Service Request' }
      ]
    },
    { id: 'visitors', label: 'Visitors', icon: UserCheck },
    { 
      id: 'reports', 
      label: 'Report', 
      icon: BarChart3, 
      hasDropdown: true,
      children: [
        { id: 'reports-financial', label: 'Financial Report' },
        { id: 'reports-maintenance', label: 'Maintenance Report' }
      ]
    },
    { id: 'settings', label: 'Settings', icon: Settings }
  ];

  return (
    <div className={`sidebar ${collapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        {!collapsed && <h2>Society Management</h2>}
        <button className="sidebar-toggle" onClick={onToggle}>
          <ChevronLeft size={20} />
        </button>
      </div>

      <ul className="nav-menu">
        {menuItems.map((item) => (
          <li key={item.id} className="nav-item">
            <a 
              href={`#${item.id}`} 
              className={`nav-link ${item.active ? 'active' : ''}`}
            >
              <item.icon className="nav-icon" size={20} />
              {!collapsed && (
                <>
                  <span className="nav-text">{item.label}</span>
                  {item.hasDropdown && (
                    <ChevronDown className="nav-arrow" size={16} />
                  )}
                </>
              )}
            </a>
            {!collapsed && item.children && (
              <ul style={{ marginLeft: '20px', marginTop: '5px' }}>
                {item.children.map((child) => (
                  <li key={child.id} className="nav-item">
                    <a href={`#${child.id}`} className="nav-link">
                      <span className="nav-text" style={{ fontSize: '12px' }}>
                        {child.label}
                      </span>
                    </a>
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
