import { apiClient, API_ENDPOINTS } from '../utils/api';

export class DashboardService {
  // Get dashboard statistics
  static async getDashboardStats() {
    try {
      const response = await apiClient.get(API_ENDPOINTS.DASHBOARD_STATS);
      return response;
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
      // Fallback to mock data if backend is not available
      return {
        towers: 3,
        unsoldApartments: 0,
        apartments: 4,
        owners: 3,
        tenants: 1,
        maintenanceDues: 12
      };
    }
  }

  // Get today's bookings
  static async getTodaysBookings() {
    try {
      // Mock data - replace with actual API call
      return [
        {
          id: 1,
          amenity: 'Swimming Pool',
          user: 'Bernadine Keeling',
          time: '02:00 PM',
          duration: '40 min'
        },
        {
          id: 2,
          amenity: 'Swimming Pool',
          user: 'Bernadine Keeling',
          time: '02:40 PM',
          duration: '40 min'
        },
        {
          id: 3,
          amenity: 'Gym',
          user: 'Bernadine Keeling',
          time: '05:00 PM',
          duration: '20 min'
        }
      ];
    } catch (error) {
      console.error('Error fetching today\'s bookings:', error);
      throw error;
    }
  }

  // Get pending rent payments
  static async getPendingPayments() {
    try {
      // Mock data - replace with actual API call
      return [
        {
          id: 1,
          apartment: '103 Nathaniel Hoppe',
          year: '2025',
          amount: '₹2,998.02',
          status: 'Unpaid'
        },
        {
          id: 2,
          apartment: '103 Nathaniel Hoppe',
          year: '2025',
          amount: '₹2,998.02',
          status: 'Unpaid'
        }
      ];
    } catch (error) {
      console.error('Error fetching pending payments:', error);
      throw error;
    }
  }

  // Get open tickets
  static async getOpenTickets() {
    try {
      // Mock data - replace with actual API call
      return [
        {
          id: '11786674',
          resident: 'Zoe Hill',
          assigned: 'Leora Ward',
          status: 'Open'
        }
      ];
    } catch (error) {
      console.error('Error fetching open tickets:', error);
      throw error;
    }
  }

  // Get utility bills due
  static async getUtilityBillsDue() {
    try {
      // Mock data - replace with actual API call
      return [
        {
          id: 1,
          bill: '101 Water Bill',
          date: '10 February 2025',
          amount: '₹300.00',
          status: 'Unpaid'
        }
      ];
    } catch (error) {
      console.error('Error fetching utility bills due:', error);
      throw error;
    }
  }

  // Get all dashboard data
  static async getAllDashboardData() {
    try {
      const [stats, bookings, payments, tickets, bills] = await Promise.all([
        this.getDashboardStats(),
        this.getTodaysBookings(),
        this.getPendingPayments(),
        this.getOpenTickets(),
        this.getUtilityBillsDue()
      ]);

      return {
        stats,
        bookings,
        payments,
        tickets,
        bills
      };
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      throw error;
    }
  }
}

export default DashboardService;
