import React from 'react';
import { Globe, Moon, Calendar } from 'lucide-react';
import { format } from 'date-fns';

const Header = () => {
  const currentDate = new Date();
  const formattedDate = format(currentDate, 'EEEE, d MMM, h:mm a');

  return (
    <div className="header">
      <div></div>
      <div className="header-right">
        <Globe size={20} color="#6c757d" />
        <span style={{ fontSize: '14px', color: '#6c757d' }}>en</span>

        <Moon size={20} color="#6c757d" />

        <div style={{ 
          width: '32px', 
          height: '32px', 
          borderRadius: '50%', 
          backgroundColor: '#2c3e50', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          color: 'white',
          fontSize: '12px',
          fontWeight: '600'
        }}>
          BK
        </div>

        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <Calendar size={16} color="#6c757d" />
          <span style={{ fontSize: '14px', color: '#6c757d' }}>
            {formattedDate}
          </span>
        </div>
      </div>
    </div>
  );
};

export default Header;
