import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  Home, 
  Users, 
  User, 
  Grid, 
  Building, 
  FileText, 
  Mail, 
  List, 
  Wrench, 
  UserCheck, 
  BarChart3, 
  Settings,
  ChevronLeft,
  ChevronDown
} from 'lucide-react';

const Sidebar = ({ collapsed, onToggle }) => {
  const location = useLocation();

  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: Home, path: '/dashboard' },
    { id: 'users', label: 'Users', icon: Users, path: '/users' },
    { id: 'apartments', label: 'Apartments', icon: Building, path: '/apartments' },
    { id: 'amenities', label: 'Amenities', icon: Grid, path: '/amenities' },
    { id: 'bills', label: 'Bills', icon: FileText, path: '/bills' },
    { id: 'tickets', label: 'Tickets', icon: Mail, path: '/tickets' },
    { id: 'notice', label: 'Notice Board', icon: List, path: '/notices' },
    { id: 'services', label: 'Services', icon: Wrench, path: '/services' },
    { id: 'visitors', label: 'Visitors', icon: UserCheck, path: '/visitors' },
    { id: 'reports', label: 'Reports', icon: BarChart3, path: '/reports' },
    { id: 'settings', label: 'Settings', icon: Settings, path: '/settings' }
  ];

  return (
    <div className={`sidebar ${collapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        {!collapsed && <h2>Society Management</h2>}
        <button className="sidebar-toggle" onClick={onToggle}>
          <ChevronLeft size={20} />
        </button>
      </div>

      <ul className="nav-menu">
        {menuItems.map((item) => (
          <li key={item.id} className="nav-item">
            <a 
              href={`#${item.id}`} 
              className={`nav-link ${item.active ? 'active' : ''}`}
            >
              <item.icon className="nav-icon" size={20} />
              {!collapsed && (
                <>
                  <span className="nav-text">{item.label}</span>
                  {item.hasDropdown && (
                    <ChevronDown className="nav-arrow" size={16} />
                  )}
                </>
              )}
            </a>
            {!collapsed && item.children && (
              <ul style={{ marginLeft: '20px', marginTop: '5px' }}>
                {item.children.map((child) => (
                  <li key={child.id} className="nav-item">
                    <a href={`#${child.id}`} className="nav-link">
                      <span className="nav-text" style={{ fontSize: '12px' }}>
                        {child.label}
                      </span>
                    </a>
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
